INSERT INTO denuncias (correlativo, lugar, tipo, descripcion, a_quien_se_reporto, tu_nombre, comision, fecha_creacion, oculta_temporalmente)
VALUES (22, 'Churrasqueras', 'Mascotas', 'Buen día, estoy reportando un problema en las Churrasqueras, se encuentra un vecino con 2 perros. El mensaje del vecino es el siguiente: "Suma y sigue… perros y sin correas en área de churrasqueras"', 'Andreina Benítez', 'Rodrigo Deras (en nombre de otro vecino)', 'Comisión de Convivencia', '2025-10-10T17:08:18.337Z', false);

-- Denuncia #21
INSERT INTO denuncias (correlativo, lugar, tipo, descripcion, a_quien_se_reporto, tu_nombre, comision, fecha_creacion, oculta_temporalmente)
VALUES (21, 'Rentas cortas (Airbnb)', 'Rentas cortas', 'Buen día, estoy reportando un problema en las Rentas cortas (Airbnb), un vecino indica lo siguiente: "Andreina Buenos dias. Es un gusto saludarle. Ayer le pase un link que lo comparto por aquí sobre un anuncio de airbnb que invita a disfrutar de las amenidades con familia y amigos". https://www.airbnb.mx/rooms/1497421630208443740?viralityEntryPoint=1&slcid=9459159cca334442a9776abdcb6eedd1&s=76&adults=1&guests=1&slug=2vNutJl5&source_impression_id=p3_1759933192_P3GjMbZtaHOt169Z Anfitrión: Zac SuperAnfitrión1 año de experiencia. Base legal: Normas de Convivencia de Parque 14 Capítulo VI – Inquilinos, Arrendatarios, Subarrendatarios y/o Usuarios en General y Arrendamientos a Corto Plazo y el Capítulo X – Uso de Áreas Comunes: Los huéspedes (arrendatarios a corto plazo) registrados (por Airbnb) no tienen derecho a recibir visitas.', 'Andreina Benítez', 'Rodrigo Deras (en nombre de otro vecino)', 'Comisión de Rentas Cortas', '2025-10-08T14:23:36.091Z', false);

-- Denuncia #20
INSERT INTO denuncias (correlativo, lugar, tipo, descripcion, a_quien_se_reporto, tu_nombre, comision, fecha_creacion, oculta_temporalmente)
VALUES (20, 'Sótano 1', 'Parqueo', 'Buen día, estoy reportando un problema en el Sótano 1, donde un vecino indica lo siguiente: "Buen día en este momento está saliendo un carro de visitas con coro número 23 lo cual significa que pernoctó la noche el auto en el parquero, habria de ver el motivo." Base legal: En el Capítulo XI – Uso de los sótanos y estacionamientos de las Normas de Convivencia de Parque 14, se establece que: Los parqueos de visitas son de uso temporal y exclusivamente para estancias diurnas o cortas; no deben utilizarse para pernoctar o dejar vehículos durante la noche, salvo autorización expresa de la administración.', 'Andreina Benítez', 'Rodrigo Deras (en nombre de otro vecino) Luis Estrada', 'Comisión de Seguridad', '2025-10-08T13:43:12.783Z', false);

