-- Denuncia #18
INSERT INTO denuncias (correlativo, lugar, tipo, descripcion, a_quien_se_reporto, tu_nombre, comision, fecha_creacion, oculta_temporalmente)
VALUES (18, 'Ingreso de vehículos', 'Parqueo', 'Buen día, estoy reportando un problema en el ingreso de visitas. Una vecina comenta que ha tenido inconvenientes con la manera en que se manejan las visitas en el edificio, estos son sus comentarios: "Le escribí a Andreina el caso pero la verdad es de cuestionar cómo lo están haciendo, he tenido 2 visitas en los últimos días y siempre llamo para dar el nombre y hacer más eficiente el ingreso, pero de igual manera cuando la visita está en garita me llaman entonces hoy le pregunté a la recepcionista que donde visualizaba ella el dato que yo misma le había dado un par de horas antes, su repuesta fue mi compañera no me lo dijo, entonces desde ahí ya genera descontrol, porque deberían de tener mapeados en un excel que aptos tienen permitidos visitas y tener ese listado digitalizado"', 'Andreina Benítez', 'Rodrigo Deras (en nombre de Jessica Jurado)', 'Comisión de Administración', '2025-10-06T22:04:13.299Z', false);

-- Denuncia #17
INSERT INTO denuncias (correlativo, lugar, tipo, descripcion, a_quien_se_reporto, tu_nombre, comision, fecha_creacion, oculta_temporalmente)
VALUES (17, 'En todo el Parque', 'Eléctricos', 'Buen día, estoy reportando un problema en en todo Parque 14, hubo un apagón por más de 45 minutos y la planta eléctrica no respondió. Varios vecinos indicaron lo siguiente: "Otra vez la planta de emergencia de Parque Américas a esta hora de la madrugada", "Me despertó el sonido y me di cuenta que tampoco tenemos luz"', 'Andreina Benítez', 'Rodrigo Deras en nombre de Ma. José', 'Comisión de Administración', '2025-10-06T12:26:51.629Z', false);

